import './App.css';
import React from 'react';

class Exo extends React.Component {
  constructor (props) {
    super(props);
    this.state = {count_object: 0};
    this.state = {count_user: 0};
  }


  add_object() {
    this.setState ({count: this.state.count_object + 1});
  }

  add_user() {
    this.setState ({count: this.state.count_user + 1});
  }

  new_box(name) {
    const box =
    <div className="box" style={{width: ((name === "Utilisateur :" ? 14.5 : 13.25) + '%')}}><p className="box-text">{name}<input></input></p></div>;
    return box;
  }

  render() {
    return (
      <div className="header">
        <h1>Exercice entretien de stage</h1>
        <button className="add"
        onClick={() => this.add_object()}
        >Ajouter un objet</button>
        <button className="add"
        onClick={() => this.add_user()}
        >Ajouter un utilisateur</button>
        <p className="list">Liste :</p>
        {Array.from({length: this.state.count_object}, item => this.new_box("Object :"))}
        {Array.from({length: this.state.count_user}, item => this.new_box("Utilisateur :"))}
      </div>
    );
  }
}

function App() {
  return <Exo />;
}

export default App;
