import './App.css';
import React from 'react';

class Exo extends React.Component {
  constructor (props) {
    super(props);    this.state =
    { counter : 0,
      array : Array(0)
    };
  }

  new_box(name) {
    const box =
    <div className="box" style={{width: ((name === "Utilisateur :" ? 14.5 : 13.25) + '%')}}><p className="box-text">{name}<input></input></p></div>;
    return box;
  }

  add_object() {
    var new_array = this.state.array;
    new_array.push(this.new_box("Object :"));

    this.setState ({counter: this.state.counter + 1});
    this.setState ({array : new_array})
  }

  add_user() {
    var new_array = this.state.array;
    new_array.push(this.new_box("Object :"));

    this.setState ({counter: this.state.counter + 1});
    this.setState ({array : new_array})
  }

  render() {
    return (
      <div className="header">
        <h1>Exercice entretien de stage</h1>
        <button className="add"
        onClick={() => this.add_object()}
        >Ajouter un objet</button>
        <button className="add"
        onClick={() => this.add_user()}
        >Ajouter un utilisateur</button>
        <p className="list">Liste :</p>
        {this.state.array}
      </div>
    );
  }
}

function App() {
  return <Exo />;
}

export default App;
