import './App.css';
import React from 'react';

class Exo extends React.Component {
  /*constructor (props) {
    super(props);
  }*/

  new_box(name, length) {
    const box =
    <div className="box" style={{width: (4 + length) + "%"}}><p className="box-text">{name}<input></input></p></div>;
    return box;
  }

  create_button(name) {
    const new_button =
      <button className="box">
        {name}
      </button>;
    return new_button;
  }

  Header() {
    return (
      <div className="header">
        <h1>Exercice entretien de stage</h1>
        <button className="add"
        onClick={() => this.create_button("Object :")}
        >Ajouter un objet</button>
        <button className="add"
        onClick={() => this.create_button("Utilisateur :")}
        >Ajouter un utilisateur</button>
        <p className="list">Liste :</p>
        {this.new_box("Object :", "Object".length)}
      </div>
    );
  }
}

function App() {
  var func = new Exo();
  return func.Header();
}

export default App;
